'use strict';

/**
 * Run the given generators and tasks. This flag is unnecessary when
 * used with [base-runner][].
 *
 * ```sh
 * # run task "foo"
 * $ app --tasks foo
 * #=> {task: ['foo']}
 * # run generator "foo", task "bar"
 * $ app --tasks foo:bar
 * #=> {task: ['foo:bar']}
 * ```
 * @name tasks
 * @api public
 * @cli public
 */

module.exports = function(app) {
  return function(tasks, next) {
    app.generateEach(tasks, next);
  };
};
